st_id`, `strategy_id`, `day_id`, `trade_id`, `bar_id`)) ENGINE = MEMORY;CREATE  TABLE IF NOT EXISTS `backtest`.`backtest_days` (    `backtest_id` INT NOT NUll ,    `day_id` INT NOT NULL ,    `open` BIGINT NOT NULL ,    `close` BIGINT NOT NULL ,    `volume` BIGINT UNSIGNED NULL ,    `tick_count` INT UNSIGNED NULL ,    PRIMARY KEY (`backtest_id`, `day_id`)  ) ENGINE = MEMORY;