m_parentId` INT NULL ,  `m_triggerMethod` TINYINT UNSIGNED NULL ,  `m_outsideRth` TINYINT(1) UNSIGNED NULL ,  `m_goodAfterTime` DATETIME NULL ,  `m_goodTillDate` DATETIME NULL ,  `m_faGroup` VARCHAR(45) NULL ,  `m_faProfile` VARCHAR(45) NULL ,  `m_faMethod` VARCHAR(45) NULL ,  `m_faPrecentage` VARCHAR(45) NULL ,  `m_allOrNone` TINYINT(1) UNSIGNED NULL ,  `m_minQty` INT NULL ,  `m_volatilityType` TINYINT UNSIGNED NULL ,  `m_trailStopPrice` DOUBLE NULL ,  `m_whatIf` TINYINT(1) UNSIGNED NULL ,  `m_status` VARCHAR(45) NULL ,  `m_initMargin` VARCHAR(45) NULL ,  `m_maintMargin` VARCHAR(45) NULL ,  `m_equityWithLoan` VARCHAR(45) NULL ,  `m_commission` DOUBLE NULL ,  `m_minCommission` DOUBLE NULL ,  `m_maxCommission` DOUBLE NULL ,  `m_commissionCurrency` VARCHAR(45) NULL ,  `m_warningText` TEXT NULL ,  `filled` INT NULL ,  `remaining` INT NULL ,  `avgFillPrice` DOUBLE NULL ,  `lastFillPrice` DOUBLE NULL ,  `whyHeld` TEXT NULL ,  `stopPrice` DOUBLE NULL ,  `trailPrice` DOUBLE NULL ,  `enterOrder` TINYINT(1) UNSIGNED NULL ,  `submitted_time_long` BIGINT NULL,  `modified_time_long` BIGINT NULL,    PRIMARY KEY (`m_permId`)) ENGINE = InnoDB;CREATE  TABLE IF NOT EXISTS `accounts` (  `m_acctNumber` VARCHAR(8) NOT NULL ,   `alias` VARCHAR(45) NULL ,  PRIMARY KEY (`m_acctNumber`)) ENGINE = InnoDB;